CREATE TABLE "public"."post_reactions" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "post_id" uuid NOT NULL, "user_id" uuid NOT NULL, "reaction_id" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("post_id") REFERENCES "public"."posts"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("user_id") REFERENCES "public"."users"("user_id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("reaction_id") REFERENCES "public"."reactions"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("post_id", "user_id"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
