CREATE TABLE "public"."profile_verfication" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "first_name" text NOT NULL, "middle_name" text NOT NULL, "last_name" text NOT NULL, "date_of_birth" text NOT NULL, "country_issue_id" text NOT NULL, "govt_issue_id" text NOT NULL, "profile_status" boolean NOT NULL DEFAULT false, "phone" text NOT NULL, "phone_status" boolean NOT NULL DEFAULT false, "email" text NOT NULL, "email_status" boolean NOT NULL DEFAULT false, "user_id" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."users"("user_id") ON UPDATE restrict ON DELETE restrict);COMMENT ON TABLE "public"."profile_verfication" IS E'verfication data needed for profile verification';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
